@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));
  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));
  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));
  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));
  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));
  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));
  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));
  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));
  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));
  --color-radius: 0.75rem;

  --font-sans: 'Inter', sans-serif;
  --font-display: 'Poppins', sans-serif;
}

:root {
  /* Primary #F95E01 (vibrant orange) -> 22 98% 49% */
  --primary: 22 98% 49%;
  --primary-foreground: 0 0% 100%;

  /* Secondary #33BBD5 (bright cyan) -> 190 65% 52% */
  --secondary: 190 65% 52%;
  --secondary-foreground: 0 0% 100%;

  /* Text #2C2C2C (charcoal) -> 0 0% 17% */
  --foreground: 0 0% 17%;
  --background: 210 17% 98%; /* #F8F9FA */

  --card: 0 0% 100%;
  --card-foreground: 0 0% 17%;
  --popover: 0 0% 100%;
  --popover-foreground: 0 0% 17%;

  --muted: 210 20% 96%;
  --muted-foreground: 215 16% 47%;

  --accent: 22 98% 96%; /* Light orange tint */
  --accent-foreground: 22 98% 49%;

  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;

  --border: 220 13% 91%;
  --input: 220 13% 91%;
  --ring: 22 98% 49%;

  --radius: 0.75rem;
}

.dark {
  --background: 224 71% 4%;
  --foreground: 210 40% 98%;
  --card: 222 47% 11%;
  --card-foreground: 210 40% 98%;
  --popover: 222 47% 11%;
  --popover-foreground: 210 40% 98%;
  --primary: 22 98% 49%;
  --primary-foreground: 0 0% 100%;
  --secondary: 190 65% 52%;
  --secondary-foreground: 0 0% 100%;
  --muted: 217 33% 17%;
  --muted-foreground: 215 20% 65%;
  --accent: 217 33% 17%;
  --accent-foreground: 210 40% 98%;
  --destructive: 0 62% 30%;
  --destructive-foreground: 210 40% 98%;
  --border: 217 33% 17%;
  --input: 217 33% 17%;
  --ring: 224 76% 48%;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
